SELECT ztbl_ZK_Tage_Zeitraum.ZK_MA_ID, ztbl_ZK_Tage_Zeitraum.ZKDatum, zqry_ZK_Anzeige_prepare.VA_ID, zqry_ZK_Anzeige_prepare.VADatum_ID, zqry_ZK_Anzeige_prepare.VAStart_ID, zqry_ZK_Anzeige_prepare.ZUO_ID, zqry_ZK_Anzeige_prepare.NV_ID, zqry_ZK_Anzeige_prepare.Korr_ID, zqry_ZK_Anzeige_prepare.MA_ID, zqry_ZK_Anzeige_prepare.VADatum, zqry_ZK_Anzeige_prepare.Veranstaltung, zqry_ZK_Anzeige_prepare.Jahr, zqry_ZK_Anzeige_prepare.Monat, zqry_ZK_Anzeige_prepare.Bemerkung, zqry_ZK_Anzeige_prepare.Beginn, zqry_ZK_Anzeige_prepare.Ende, zqry_ZK_Stunden_Kreuztabelle.*, zqry_ZK_Anzeige_prepare.SummevonWert AS Wert, zqry_ZK_Anzeige_prepare.gesperrt, zqry_ZK_Anzeige_prepare.exportieren, zqry_ZK_Anzeige_prepare.exportiert
FROM (zqry_ZK_Anzeige_prepare LEFT JOIN zqry_ZK_Stunden_Kreuztabelle ON zqry_ZK_Anzeige_prepare.Kreuz_KEY = zqry_ZK_Stunden_Kreuztabelle.Kreuz_KEY) RIGHT JOIN ztbl_ZK_Tage_Zeitraum ON (zqry_ZK_Anzeige_prepare.MA_ID = ztbl_ZK_Tage_Zeitraum.[ZK_MA_ID]) AND (zqry_ZK_Anzeige_prepare.VADatum = ztbl_ZK_Tage_Zeitraum.ZKDatum)
ORDER BY ztbl_ZK_Tage_Zeitraum.ZKDatum;
