SELECT qry_DP_Alle.*, 1 AS Hlp FROM qry_DP_Alle WHERE (((qry_DP_Alle.VADatum) Between #2025-11-25# AND #2025-12-01#) AND ((qry_DP_Alle.MA_ID)>0)) ORDER BY qry_DP_Alle.MAName, qry_DP_Alle.VADatum, qry_DP_Alle.MA_Start, qry_DP_Alle.MA_Ende UNION SELECT -1 AS VA_ID,  (([tbl_MA_Zeittyp].ID) * -1) AS ZuordID, 1 AS Anz_MA, tbl_MA_Zeittyp.ZeitTyp AS ObjOrt, CDate(Fix(CDbl([vonDat]))) AS VADatum, 1 AS Pos_Nr, Format(tbl_MA_NVerfuegZeiten.vonDat, 'hh:nn',2) AS MA_Start, Format(tbl_MA_NVerfuegZeiten.bisDat, 'hh:nn', 2) AS MA_Ende, tbl_MA_NVerfuegZeiten.MA_ID, [Nachname] & ' ' & [Vorname] AS MAName, 0 AS IstFraglich, 1 AS Hlp FROM tbl_MA_Zeittyp INNER JOIN (tbl_MA_Mitarbeiterstamm RIGHT JOIN tbl_MA_NVerfuegZeiten ON tbl_MA_Mitarbeiterstamm.ID = tbl_MA_NVerfuegZeiten.MA_ID) ON tbl_MA_Zeittyp.Kuerzel_Datev = tbl_MA_NVerfuegZeiten.Zeittyp_ID WHERE (((CDate(Fix(CDbl([vonDat])))) Between #2025-11-25# AND #2025-12-01#));

