SELECT tbltmp_MA_Monat_Einzel.MA_ID, tbl_MA_Mitarbeiterstamm.Nachname, tbl_MA_Mitarbeiterstamm.Vorname, tbl_MA_Mitarbeiterstamm.IstAktiv, tbl_MA_Mitarbeiterstamm.IstSubunternehmer, tbltmp_MA_Monat_Einzel.AktDat, Day([Aktdat]) AS TagNr, tbltmp_MA_Monat_Einzel.Auftrag_Ort, Left(CStr(Nz([MA_Start])),5) AS Beginn, Left(CStr(Nz([MA_Ende])),5) AS Ende, tbltmp_MA_Monat_Einzel.Brutto_Std, tbltmp_MA_Monat_Einzel.Netto_Std, tbltmp_MA_Monat_Einzel.Fahrtko, tbltmp_MA_Monat_Einzel.RL_34a, tbltmp_MA_Monat_Einzel.[34a_RZ], tbltmp_MA_Monat_Einzel.Kaution, tbltmp_MA_Monat_Einzel.Nicht_Erscheinen, tbltmp_MA_Monat_Einzel.Abschlag, tbltmp_MA_Monat_Einzel.Sonst_Abzuege, tbltmp_MA_Monat_Einzel.Monatslohn, tbltmp_MA_Monat_Einzel.Dienstkleidung, tbltmp_MA_Monat_Einzel.Sonst_Abzuege_Grund, tbltmp_MA_Monat_Einzel.UeberwVon, tbltmp_MA_Monat_Einzel.Bemerkungen, tbltmp_MA_Monat_Einzel.VA_ID, tbltmp_MA_Monat_Einzel.VAStart_ID
FROM tbl_MA_Mitarbeiterstamm INNER JOIN tbltmp_MA_Monat_Einzel ON tbl_MA_Mitarbeiterstamm.ID = tbltmp_MA_Monat_Einzel.MA_ID
ORDER BY tbltmp_MA_Monat_Einzel.AktDat;

