SELECT DISTINCT tbl_MA_VA_Zuordnung.VA_ID, tbl_MA_VA_Zuordnung.VADatum_ID, tbl_MA_VA_Zuordnung.VAStart_ID, tbl_MA_VA_Zuordnung.MA_ID, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.MA_Start, tbl_MA_VA_Zuordnung.MA_Ende, tbl_VA_Start.MA_Anzahl, tbl_MA_Mitarbeiterstamm.IstSubunternehmer, [Nachname] & " " & [Vorname] AS Name, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Ort, tbl_VA_Auftragstamm.Objekt, tbl_VA_Start.VA_Treffpunkt, tbl_VA_Auftragstamm.Treffpunkt, tbl_VA_Start.VA_Zeitpunkt, tbl_VA_Auftragstamm.Treffp_Zeit, tbl_VA_Auftragstamm.Dienstkleidung, tbl_KD_Kundenstamm.kun_Firma, tbl_VA_Auftragstamm.Ansprechpartner
FROM (tbl_KD_Kundenstamm RIGHT JOIN tbl_VA_Auftragstamm ON tbl_KD_Kundenstamm.kun_Id = tbl_VA_Auftragstamm.Veranstalter_ID) INNER JOIN (tbl_VA_AnzTage INNER JOIN ((tbl_MA_VA_Zuordnung INNER JOIN tbl_MA_Mitarbeiterstamm ON tbl_MA_VA_Zuordnung.MA_ID = tbl_MA_Mitarbeiterstamm.ID) INNER JOIN tbl_VA_Start ON tbl_MA_VA_Zuordnung.VAStart_ID = tbl_VA_Start.ID) ON tbl_VA_AnzTage.ID = tbl_MA_VA_Zuordnung.VADatum_ID) ON tbl_VA_Auftragstamm.ID = tbl_VA_AnzTage.VA_ID
ORDER BY tbl_MA_VA_Zuordnung.MA_ID, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.MA_Start;

