SELECT qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.kun_ID, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VA_ID, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VADatum, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VAStart_ID, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.MA_Start, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.MA_Ende, Sum(qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.MA_Brutto_Std) AS Menge, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.PreisArt_ID, tbl_VA_Preise.EurPreis AS EzPreis, tbl_VA_Preise.MwStSatz AS MwSt
FROM qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std LEFT JOIN tbl_VA_Preise ON (qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VA_ID = tbl_VA_Preise.VA_ID) AND (qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.PreisArt_ID = tbl_VA_Preise.Kostenzuo_KD)
GROUP BY qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.kun_ID, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VA_ID, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VADatum, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.VAStart_ID, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.MA_Start, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.MA_Ende, qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.PreisArt_ID, tbl_VA_Preise.EurPreis, tbl_VA_Preise.MwStSatz
HAVING (((qry_RchAng_VA_MA_Alle_Pos_MA_Einzel_Std.kun_ID)=Get_Priv_Property("prp_Rechnung_AktKunde")));

