SELECT qry_Report_Auftrag_Sort.VADatum AS Datum, qry_Report_Auftrag_Sort.VADatum, qry_Report_Auftrag_Sort.VADatum_ID, qry_Report_Auftrag_Sort.ID, qry_Report_Auftrag_Sort.Auftrag, qry_Report_Auftrag_Sort.Veranstalter_ID, qry_Report_Auftrag_Sort.Objekt, qry_Report_Auftrag_Sort.Strasse, qry_Report_Auftrag_Sort.PLZ, qry_Report_Auftrag_Sort.Ort, qry_Report_Auftrag_Sort.Dat_VA_Von, qry_Report_Auftrag_Sort.Dat_VA_Bis, qry_Report_Auftrag_Sort.Dummy, qry_Report_Auftrag_Sort.Bemerkungen, qry_Report_Auftrag_Sort.Rch_Dat, qry_Report_Auftrag_Sort.Rch_Nr, qry_Report_Auftrag_Sort.Bemerkung, qry_Report_Auftrag_Sort.Treffpunkt, qry_Report_Auftrag_Sort.Treffpunkt2, qry_Report_Auftrag_Sort.Treffp_Zeit, qry_Report_Auftrag_Sort.Dienstkleidung, qry_Report_Auftrag_Sort.Ansprechpartner, qry_Report_Auftrag_Sort.Veranst_Status_ID, qry_Report_Auftrag_Sort.Objekt_ID, qry_Report_Auftrag_Sort.Erst_von, qry_Report_Auftrag_Sort.Erst_am, qry_Report_Auftrag_Sort.Aend_von, qry_Report_Auftrag_Sort.Aend_am, qry_Report_Auftrag_Sort.AnzTg, qry_Report_Auftrag_Sort.Excel_Dateiname, qry_Report_Auftrag_Sort.Excel_Path, qry_Report_Auftrag_Sort.Abschlussdatum, Nz([PKW_Anz]) AS PKW_Anzahl, tbl_MA_VA_Zuordnung.MA_ID, Null AS PosNr, Left([MA_Start],5) AS Start, [Nachname] & " " & [Vorname] AS Gesname, tbl_MA_VA_Zuordnung.MA_Netto_Std, timeberech_G([tbl_MA_VA_Zuordnung].[VADatum],[MA_Start],[MA_Ende]) AS MA_Brutto_Std, IIf(Nz([tbl_MA_VA_Zuordnung].[PKW],0)>0,"PKW"," ") AS PKW, tbl_MA_VA_Zuordnung.Bemerkungen AS sub_Bem, tbl_MA_VA_Zuordnung.RL_34a, "" AS Status, tbl_MA_VA_Zuordnung.IstFraglich
FROM qry_Report_Auftrag_Sort INNER JOIN (tbl_MA_VA_Zuordnung LEFT JOIN tbl_MA_Mitarbeiterstamm ON tbl_MA_VA_Zuordnung.MA_ID = tbl_MA_Mitarbeiterstamm.ID) ON (qry_Report_Auftrag_Sort.ID = tbl_MA_VA_Zuordnung.VA_ID) AND (qry_Report_Auftrag_Sort.VADatum_ID = tbl_MA_VA_Zuordnung.VADatum_ID)
