SELECT tbl_VA_Auftragstamm.ID, tbl_VA_Auftragstamm.Auftrag, ([Objekt]) AS Location, tbl_VA_Start.VADatum AS Datum, tbl_VA_Auftragstamm.Ort, tbl_MA_VA_Zuordnung.MA_ID, tbl_MA_VA_Zuordnung.MA_Start, tbl_MA_VA_Zuordnung.MA_Ende, ([MA_Brutto_Std2]) AS Anwesend, tbl_MA_VA_Zuordnung.MA_Netto_Std2, ([nachname] & " " & [Vorname]) AS Name, tbl_MA_VA_Zuordnung.PosNr, tbl_MA_Mitarbeiterstamm.ID
FROM tbl_VA_Start INNER JOIN (tbl_VA_Auftragstamm INNER JOIN (tbl_MA_VA_Zuordnung LEFT JOIN tbl_MA_Mitarbeiterstamm ON tbl_MA_VA_Zuordnung.MA_ID = tbl_MA_Mitarbeiterstamm.ID) ON tbl_VA_Auftragstamm.ID = tbl_MA_VA_Zuordnung.VA_ID) ON (tbl_VA_Start.VADatum_ID = tbl_MA_VA_Zuordnung.VADatum_ID) AND (tbl_VA_Start.VA_ID = tbl_MA_VA_Zuordnung.VA_ID) AND (tbl_VA_Start.MVA_Start = tbl_MA_VA_Zuordnung.MVA_Start) AND (tbl_VA_Start.MVA_Ende = tbl_MA_VA_Zuordnung.MVA_Ende)
GROUP BY tbl_VA_Auftragstamm.ID, tbl_VA_Auftragstamm.Auftrag, ([Objekt]), tbl_VA_Start.VADatum, tbl_VA_Auftragstamm.Ort, tbl_MA_VA_Zuordnung.MA_ID, tbl_MA_VA_Zuordnung.MA_Start, tbl_MA_VA_Zuordnung.MA_Ende, ([MA_Brutto_Std2]), tbl_MA_VA_Zuordnung.MA_Netto_Std2, ([nachname] & " " & [Vorname]), tbl_MA_VA_Zuordnung.PosNr, tbl_MA_Mitarbeiterstamm.ID;

