SELECT tbl_Rch_Kopf.ID AS Rch_ID, tbl_Rch_Kopf.RchNr_Ext, Format([RchDatum],"ddd dd/mm/yyyy") AS Rechnungsdatum, tbl_Rch_Kopf.kun_ID, tbl_Rch_Kopf.MA_ID, tbl_Rch_Kopf.Dateiname, tbl_Rch_Kopf.IstSammelRch, Format([Leist_Datum_von],"ddd  dd/mm/yyyy") AS Leist_Dat_von, Format([Leist_Datum_bis],"ddd  dd/mm/yyyy") AS Leist_Dat_bis, Format([Ang_Gueltig_Bis],"ddd  dd-mm-yyyy") AS Ang_Guelt_Bis, FormatCurrency([Zwi_Sum1]) AS Zwi_Sum, FormatCurrency([MwSt19_Sum1]) AS MwSt19_Sum, FormatCurrency([MwSt7_Sum1]) AS MwSt7_Sum, FormatCurrency([Gesamtsumme1]) AS Gesamtsumme, tbl_Rch_Kopf.ZahlBed_ID, Zahlbed_Text([Zahlbed_ID],[Gesamtsumme1]) AS ZahlBed_Text, FormatCurrency([ZahlBetrag_Netto1]) AS ZahlBetrag_Netto, tbl_Rch_Kopf.Zahlung_Bis, tbl_Rch_Kopf.IstBezahlt, tbl_Rch_Kopf.Zahlung_am, FormatCurrency([Zahlbetrag1]) AS Zahlbetrag, tbl_Rch_Kopf.Auftrag, tbl_Rch_Kopf.M1MahnDok, Format([M1Mahndat],"ddd dd/mm/yyyy") AS M1Mahndatum, tbl_Rch_Kopf.M1MahnVon, CLng([M1IstGemahnt1]) AS M1IstGemahnt, FormatCurrency([M1Mahnbetrag1]) AS M1Mahnbetrag, tbl_Rch_Kopf.M2MahnDok, Format([M2Mahndat],"ddd dd/mm/yyyy") AS M2Mahndatum, tbl_Rch_Kopf.M2MahnVon, FormatCurrency([M2Mahnbetrag1]) AS M2Mahnbetrag, CLng([M2IstGemahnt1]) AS M2IstGemahnt, tbl_Rch_Kopf.M3MahnDok, Format([M3Mahndat],"ddd dd/mm/yyyy") AS M3Mahndatum, tbl_Rch_Kopf.M3MahnVon, FormatCurrency([M3Mahnbetrag1]) AS M3Mahnbetrag, CLng([M3IstGemahnt1]) AS M3IstGemahnt, tbl_Rch_Kopf.Erst_von, tbl_Rch_Kopf.Erst_am, tbl_Rch_Kopf.Aend_von, tbl_Rch_Kopf.Aend_am, qry_Textbaustein_Kunde.*
FROM tbl_Rch_Kopf LEFT JOIN qry_Textbaustein_Kunde ON tbl_Rch_Kopf.kun_ID = qry_Textbaustein_Kunde.kun_Id;

