SELECT tbl_VA_Status.*, tbl_VA_Auftragstamm.*, tbl_VA_Status.Fortschritt, tbl_VA_Status.ID, tbl_MA_VA_Zuordnung.MA_Start, tbl_MA_VA_Zuordnung.MA_Ende, tbl_MA_VA_Zuordnung.MA_ID, tbl_VA_Auftragstamm.Dat_VA_Bis, qry_Report_Auftrag_Kopf.Rch_Nr, tbl_VA_Status.Fortschritt
FROM (((tbl_VA_Auftragstamm INNER JOIN tbl_VA_Status ON tbl_VA_Auftragstamm.Veranst_Status_ID = tbl_VA_Status.ID) INNER JOIN tbl_MA_VA_Zuordnung ON tbl_VA_Auftragstamm.ID = tbl_MA_VA_Zuordnung.VA_ID) INNER JOIN tbl_MA_Mitarbeiterstamm ON tbl_MA_VA_Zuordnung.MA_ID = tbl_MA_Mitarbeiterstamm.ID) INNER JOIN qry_Report_Auftrag_Kopf ON (qry_Report_Auftrag_Kopf.Veranstalter_ID = tbl_VA_Auftragstamm.Veranstalter_ID) AND (qry_Report_Auftrag_Kopf.Auftrag = tbl_VA_Auftragstamm.Auftrag) AND (tbl_VA_Status.ID = qry_Report_Auftrag_Kopf.ID)
WHERE (((tbl_VA_Status.ID)=4) AND ((qry_Report_Auftrag_Kopf.Rch_Nr)>"0")) OR (((tbl_VA_Status.ID)=1) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)>=Now())) OR (((tbl_VA_Status.ID)=3) AND ((tbl_MA_VA_Zuordnung.MA_Start)>0) AND ((tbl_MA_VA_Zuordnung.MA_Ende)>0) AND ((tbl_MA_VA_Zuordnung.MA_ID)>0) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)<Now())) OR (((tbl_VA_Status.ID)=2) AND ((tbl_MA_VA_Zuordnung.MA_Start)>0) AND ((tbl_MA_VA_Zuordnung.MA_Ende)>0) AND ((tbl_MA_VA_Zuordnung.MA_ID)=0) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)<Now())) OR (((tbl_VA_Status.ID)=2) AND ((tbl_MA_VA_Zuordnung.MA_Start)>0) AND ((tbl_MA_VA_Zuordnung.MA_Ende)=0) AND ((tbl_MA_VA_Zuordnung.MA_ID)>0) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)<Now())) OR (((tbl_VA_Status.ID)=2) AND ((tbl_MA_VA_Zuordnung.MA_Start)=0) AND ((tbl_MA_VA_Zuordnung.MA_Ende)>0) AND ((tbl_MA_VA_Zuordnung.MA_ID)>0) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)<Now())) OR (((tbl_VA_Status.ID)=2) AND ((tbl_MA_VA_Zuordnung.MA_Start)=0) AND ((tbl_MA_VA_Zuordnung.MA_Ende)=0) AND ((tbl_MA_VA_Zuordnung.MA_ID)>0) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)<Now())) OR (((tbl_VA_Status.ID)=2) AND ((tbl_MA_VA_Zuordnung.MA_Start)>0) AND ((tbl_MA_VA_Zuordnung.MA_Ende)=0) AND ((tbl_MA_VA_Zuordnung.MA_ID)=0) AND ((tbl_VA_Auftragstamm.Dat_VA_Bis)<Now()))
ORDER BY tbl_VA_Status.ID;

