sql.raw:
SELECT tbl_VA_Auftragstamm.ID AS VA_ID, Format([Dat_VA_Von],"dd/mm/yyyy",2.2) AS DatumVonStr, Format([Dat_VA_Bis],"dd/mm/yyyy",2.2) AS DatumBisStr, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Ort, tbl_VA_Auftragstamm.Objekt AS Location, Nz(Format([Treffp_Zeit],"Short Time")) AS TreffpZeitStr, tbl_VA_Auftragstamm.Treffpunkt AS TreffpOrtstr, tbl_VA_Auftragstamm.Dienstkleidung, tbl_KD_Kundenstamm.kun_Firma AS Auftraggeber, FormatCurrency(Nz([qry_KD_StdPreis_1].[StdPreis])) AS Std_satz_KD_1_Nettostr, FormatCurrency(Nz([qry_KD_StdPreis_3].[StdPreis])) AS Std_satz_KD_3_Nettostr, FormatCurrency(Nz([qry_KD_StdPreis_4].[StdPreis])) AS Fahrtkost_KD_NettoStr, FormatCurrency(Get_Priv_Property("prp_Lohn_Notfall")) AS Std_Lohn_nettostr, tbl_VA_Auftragstamm.Ansprechpartner
FROM (((tbl_VA_Auftragstamm LEFT JOIN tbl_KD_Kundenstamm ON tbl_VA_Auftragstamm.Veranstalter_ID = tbl_KD_Kundenstamm.kun_Id) LEFT JOIN qry_KD_StdPreis_1 ON tbl_VA_Auftragstamm.Veranstalter_ID = qry_KD_StdPreis_1.kun_ID) LEFT JOIN qry_KD_StdPreis_3 ON tbl_VA_Auftragstamm.Veranstalter_ID = qry_KD_StdPreis_3.kun_ID) LEFT JOIN qry_KD_StdPreis_4 ON tbl_VA_Auftragstamm.Veranstalter_ID = qry_KD_StdPreis_4.kun_ID
ORDER BY tbl_VA_Auftragstamm.Dat_VA_Von DESC;
