sql.raw:
SELECT tbl_MA_NVerfuegZeiten.MA_ID, 0 AS VA_ID, 0 AS VA_Datum_ID, 0 AS VA_Start_ID, CDate(Fix([vondat])) AS VADatum, Format([vondat],"Short Time") AS MA_Start, Format([bisdat],"Short Time") AS MA_Ende, 1 AS MA_Anzahl, tbl_MA_Mitarbeiterstamm.IstSubunternehmer, [Nachname] & " " & [Vorname] AS Name, tbl_MA_Zeittyp.Zeittyp AS Auftrag, "" AS Ort, "" AS Objekt, " " AS VA_Treffpunkt, " " AS Treffpunkt, Null AS VA_Zeitpunkt, Null AS Treffp_Zeit, "" AS Dienstkleidung, "" AS Firma, "" AS Ansprechpartner
FROM tbl_MA_Mitarbeiterstamm INNER JOIN (tbl_MA_Zeittyp INNER JOIN tbl_MA_NVerfuegZeiten ON tbl_MA_Zeittyp.Kuerzel_Datev = tbl_MA_NVerfuegZeiten.Zeittyp_ID) ON tbl_MA_Mitarbeiterstamm.ID = tbl_MA_NVerfuegZeiten.MA_ID
WHERE (((tbl_MA_Zeittyp.ID)=5 Or (tbl_MA_Zeittyp.ID)=7));
