SELECT qry_MA_VA_Doppelt1.*, qry_MA_VA_Doppelt2.AnzahlvonMA_ID AS Anzahl
FROM qry_MA_VA_Doppelt2 INNER JOIN qry_MA_VA_Doppelt1 ON (qry_MA_VA_Doppelt2.MA_ID = qry_MA_VA_Doppelt1.MA_ID) AND (qry_MA_VA_Doppelt2.VADatum = qry_MA_VA_Doppelt1.VADatum)
ORDER BY qry_MA_VA_Doppelt1.VADatum, qry_MA_VA_Doppelt1.MA_ID;

SELECT [_Auswerung_Sub_JJJJ_Kreuztabelle].kun_ID, [_Auswerung_Sub_JJJJ_Kreuztabelle].kun_Firma, [_Auswerung_Sub_JJJJ_Kreuztabelle].RchJahr, [_Auswerung_Sub_JJJJ_Kreuztabelle].Jahressumme_Kunde, [_Auswertung_Sub_Kundenpreise].StdPreis, [_Auswerung_Sub_JJJJ_Kreuztabelle].[1], [_Auswerung_Sub_JJJJ_Kreuztabelle].[2], [_Auswerung_Sub_JJJJ_Kreuztabelle].[3], [_Auswerung_Sub_JJJJ_Kreuztabelle].[4], [_Auswerung_Sub_JJJJ_Kreuztabelle].[5], [_Auswerung_Sub_JJJJ_Kreuztabelle].[6], [_Auswerung_Sub_JJJJ_Kreuztabelle].[7], [_Auswerung_Sub_JJJJ_Kreuztabelle].[8], [_Auswerung_Sub_JJJJ_Kreuztabelle].[9], [_Auswerung_Sub_JJJJ_Kreuztabelle].[10], [_Auswerung_Sub_JJJJ_Kreuztabelle].[11], [_Auswerung_Sub_JJJJ_Kreuztabelle].[12]
FROM _Auswerung_Sub_JJJJ_Kreuztabelle LEFT JOIN _Auswertung_Sub_Kundenpreise ON [_Auswerung_Sub_JJJJ_Kreuztabelle].kun_ID = [_Auswertung_Sub_Kundenpreise].kun_Id
ORDER BY [_Auswerung_Sub_JJJJ_Kreuztabelle].RchJahr DESC , [_Auswerung_Sub_JJJJ_Kreuztabelle].Jahressumme_Kunde DESC;

SELECT tbl_Rch_Kopf.kun_ID, tbl_KD_Kundenstamm.kun_Firma, tbl_Rch_Kopf.RchDatum, tbl_Rch_Kopf.Zwi_Sum1 AS NettoWert, CLng(Year([RchDatum]) & Right("00" & Month([RchDatum]),2)) AS RchJaMon, Year([RchDatum]) AS RchJahr, Month([RchDatum]) AS RchMonat
FROM tbl_Rch_Kopf INNER JOIN tbl_KD_Kundenstamm ON tbl_Rch_Kopf.kun_ID = tbl_KD_Kundenstamm.kun_Id
ORDER BY tbl_Rch_Kopf.RchDatum;

SELECT DISTINCTROW [Kunden/Mit Index].*, DCount("*","[Bestellungen]","[Empfänger]='" & [Firma] & "'") AS AnzBest
FROM [Kunden/Mit Index]
ORDER BY [Kunden/Mit Index].Firma;

SELECT DISTINCTROW [Kunden/Ohne Index].*, DCount("*","[Bestellungen]","[Empfänger]='" & [Firma] & "'") AS AnzBest
FROM [Kunden/Ohne Index]
ORDER BY [Kunden/Ohne Index].Firma;

SELECT DISTINCTROW Month([Bestelldatum]) AS Monat, Bestellungen.Empfänger, Bestellungen.Straße, Bestellungen.PLZ, Bestellungen.Ort, DCount("*","[Bestellungen]","[Empfänger]='" & [Empfänger] & "'") AS AnzBest
FROM Bestellungen
GROUP BY Month([Bestelldatum]), Bestellungen.Empfänger, Bestellungen.Straße, Bestellungen.PLZ, Bestellungen.Ort;

SELECT [_tblAlleTage].dtDatum AS VADatum, tbl_VA_AnzTage.ID, tbl_VA_AnzTage.VA_ID, tbl_VA_AnzTage.TVA_Soll, tbl_VA_AnzTage.TVA_Ist, tbl_VA_AnzTage.TVA_Offen, tbl_VA_AnzTage.PKW_Anzahl
FROM tbl_VA_AnzTage RIGHT JOIN _tblAlleTage ON tbl_VA_AnzTage.VADatum = [_tblAlleTage].dtDatum
WHERE ((([_tblAlleTage].dtDatum) Between #12/1/2015# And #12/31/2015#));

SELECT [_tblAlleTage].TagNr, [_tblAlleTage].dtDatum
FROM _tblAlleTage
WHERE (([_tblAlleTage].JahrNr= 2022) AND ([_tblAlleTage].MonatNr= 10));

SELECT tbl_VA_Auftragstamm.ID AS VA_ID, tbl_VA_AnzTage.ID AS VADatum_ID, tbl_VA_AnzTage.VADatum, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Objekt
FROM tbl_VA_Auftragstamm LEFT JOIN tbl_VA_AnzTage ON tbl_VA_Auftragstamm.ID = tbl_VA_AnzTage.VA_ID
WHERE (((tbl_VA_AnzTage.TVA_Offen)=True));

SELECT tbl_VA_AnzTage.VADatum, Count(tbl_VA_AnzTage.VADatum) AS AnzTag
FROM tbl_VA_AnzTage
GROUP BY tbl_VA_AnzTage.VADatum;

SELECT qry_Anz_MA_VA_Zuordnung.VA_ID, qry_Anz_MA_VA_Zuordnung.VADatum, qry_Anz_MA_VA_Zuordnung.VADatum_ID, tbl_VA_Start.VA_Start, qry_Anz_MA_VA_Zuordnung.VAStart_ID, qry_Anz_MA_VA_Zuordnung.PosNrMax, [AnzMA_Z]-[MA_Anzahl] AS Diff_MA
FROM qry_Anz_MA_VA_Zuordnung INNER JOIN tbl_VA_Start ON (qry_Anz_MA_VA_Zuordnung.VAStart_ID = tbl_VA_Start.ID) AND (qry_Anz_MA_VA_Zuordnung.VADatum_ID = tbl_VA_Start.VADatum_ID) AND (qry_Anz_MA_VA_Zuordnung.VA_ID = tbl_VA_Start.VA_ID)
WHERE ((([AnzMA_Z]-[MA_Anzahl])>0));

SELECT qry_Anz_MA_VA_Zuordnung_Tag.VA_ID, qry_Anz_MA_VA_Zuordnung_Tag.VADatum, qry_Anz_MA_VA_Zuordnung_Tag.VADatum_ID, qry_Anz_MA_VA_Zuordnung_Tag.AnzMA_Z, qry_Anz_MA_VA_Zuordnung_Tag.PosNrMax, [AnzMA_Z]-[SummevonMA_Anzahl] AS MA_Diff
FROM qry_Anz_VA_Start_Tag INNER JOIN qry_Anz_MA_VA_Zuordnung_Tag ON (qry_Anz_MA_VA_Zuordnung_Tag.VADatum_ID = qry_Anz_VA_Start_Tag.VADatum_ID) AND (qry_Anz_VA_Start_Tag.VA_ID = qry_Anz_MA_VA_Zuordnung_Tag.VA_ID)
WHERE ((([AnzMA_Z]-[SummevonMA_Anzahl])>0));

SELECT qry_Anz_MA_Anzahl_Diff_Tag.VA_ID, qry_Anz_MA_Anzahl_Diff_Tag.VADatum, qry_Anz_MA_Anzahl_Diff_Tag.VADatum_ID, qry_Anz_MA_Anzahl_Diff_Tag.AnzMA_Z, qry_Anz_MA_Anzahl_Diff_Tag.PosNrMax, [MA_Diff]-Nz([SummevonDiff_MA],0) AS Diff
FROM qry_Anz_MA_Anzahl_Diff_Tag LEFT JOIN qry_Anz_MA_Anzahl_Diff_Zwi ON (qry_Anz_MA_Anzahl_Diff_Tag.VADatum_ID = qry_Anz_MA_Anzahl_Diff_Zwi.VADatum_ID) AND (qry_Anz_MA_Anzahl_Diff_Tag.VA_ID = qry_Anz_MA_Anzahl_Diff_Zwi.VA_ID)
WHERE ((([MA_Diff]-Nz([SummevonDiff_MA],0))>0));

SELECT qry_Anz_MA_Anzahl_Diff.VA_ID, qry_Anz_MA_Anzahl_Diff.VADatum, qry_Anz_MA_Anzahl_Diff.VADatum_ID, Sum(qry_Anz_MA_Anzahl_Diff.Diff_MA) AS SummevonDiff_MA
FROM qry_Anz_MA_Anzahl_Diff
GROUP BY qry_Anz_MA_Anzahl_Diff.VA_ID, qry_Anz_MA_Anzahl_Diff.VADatum, qry_Anz_MA_Anzahl_Diff.VADatum_ID;

SELECT qry_Anz_Plan_1_2_MA.VA_ID, qry_Anz_Plan_1_2_MA.VADatum_ID, qry_Anz_Plan_1_2_MA.VAStart_ID, qry_Anz_Plan_1_2_MA.VA_Start, qry_Anz_Plan_1_2_MA.VA_Ende, qry_Anz_Plan_1_2_MA.MA_Plan, qry_Anz_Plan_1_2_MA.MA_Soll, [qry:Anz_Ist_MA].MA_Ist
FROM [qry:Anz_Ist_MA] RIGHT JOIN qry_Anz_Plan_1_2_MA ON ([qry:Anz_Ist_MA].VAStart_ID = qry_Anz_Plan_1_2_MA.VAStart_ID) AND ([qry:Anz_Ist_MA].VADatum_ID = qry_Anz_Plan_1_2_MA.VADatum_ID) AND ([qry:Anz_Ist_MA].VA_ID = qry_Anz_Plan_1_2_MA.VA_ID)
GROUP BY qry_Anz_Plan_1_2_MA.VA_ID, qry_Anz_Plan_1_2_MA.VADatum_ID, qry_Anz_Plan_1_2_MA.VAStart_ID, qry_Anz_Plan_1_2_MA.VA_Start, qry_Anz_Plan_1_2_MA.VA_Ende, qry_Anz_Plan_1_2_MA.MA_Plan, qry_Anz_Plan_1_2_MA.MA_Soll, [qry:Anz_Ist_MA].MA_Ist;

SELECT qry_Anz_Ma_Neu_Hour_1.VA_ID, qry_Anz_Ma_Neu_Hour_1.VADatum_ID, fZeitAusg([Start],[Ende],[MA_Anzahl_Ist],[MA_Anzahl]) AS Zeit
FROM qry_Anz_Ma_Neu_Hour_1
ORDER BY qry_Anz_Ma_Neu_Hour_1.VADatum_ID, fZeitAusg([Start],[Ende],[MA_Anzahl_Ist],[MA_Anzahl]);

SELECT qry_Anz_Ma_Neu_Hour_2.VA_ID, qry_Anz_Ma_Neu_Hour_2.VADatum_ID, qry_Anz_Ma_Neu_Hour_2.Zeit
FROM qry_Anz_Ma_Neu_Hour_2 INNER JOIN tbltmp_VA_Tag_All ON (qry_Anz_Ma_Neu_Hour_2.VADatum_ID = tbltmp_VA_Tag_All.VADatum_ID) AND (qry_Anz_Ma_Neu_Hour_2.VA_ID = tbltmp_VA_Tag_All.VA_ID)
ORDER BY qry_Anz_Ma_Neu_Hour_2.VA_ID, qry_Anz_Ma_Neu_Hour_2.VADatum_ID, qry_Anz_Ma_Neu_Hour_2.Zeit;

INSERT INTO tbltmp_VA_Tag_Hour_All
SELECT qry_Anz_Ma_Neu_Hour_2.*
FROM qry_Anz_Ma_Neu_Hour_2 INNER JOIN tbltmp_VA_Tag_All ON (qry_Anz_Ma_Neu_Hour_2.VADatum_ID = tbltmp_VA_Tag_All.VADatum_ID) AND (qry_Anz_Ma_Neu_Hour_2.VA_ID = tbltmp_VA_Tag_All.VA_ID);

SELECT tbl_VA_Start.VA_ID, tbl_VA_Start.VADatum_ID, tbl_VA_Start.ID AS VAStart_ID, tbl_VA_Start.VADatum, tbl_VA_Start.VA_Start, tbl_VA_Start.VA_Ende, 0+Nz([MA_IstAnz],0) AS MA_Ist, tbl_VA_Start.MA_Anzahl AS MA_Soll, tbl_VA_Start.MVA_Start, tbl_VA_Start.MVA_Ende
FROM qry_Anz_MA_Start_sub RIGHT JOIN tbl_VA_Start ON qry_Anz_MA_Start_sub.VAStart_ID = tbl_VA_Start.ID
WHERE (((tbl_VA_Start.VA_ID)=Get_Priv_Property("prp_Report1_Auftrag_ID")) AND ((tbl_VA_Start.VADatum_ID)=Get_Priv_Property("prp_Report1_Auftrag_VADatum_ID")));

SELECT qry_Anz_MA_Tag_1.VA_ID, qry_Anz_MA_Tag_1.VADatum_ID, qry_Anz_MA_Tag_1.VADatum, qry_Anz_MA_Tag_1.Auftrag, qry_Anz_MA_Tag_1.Objekt_Ort, Sum(qry_Anz_MA_Tag_1.MA_Plan_Ges) AS MA_Plan_Ges, Sum(qry_Anz_MA_Tag_1.MA_Soll_Ges) AS MA_Soll_Ges, Sum(qry_Anz_MA_Tag_1.MA_Ist_Ges) AS MA_Ist_Ges, qry_Anz_MA_Tag_1.TVA_Offen
FROM qry_Anz_MA_Tag_1
GROUP BY qry_Anz_MA_Tag_1.VA_ID, qry_Anz_MA_Tag_1.VADatum_ID, qry_Anz_MA_Tag_1.VADatum, qry_Anz_MA_Tag_1.Auftrag, qry_Anz_MA_Tag_1.Objekt_Ort, qry_Anz_MA_Tag_1.TVA_Offen;

SELECT DISTINCT qry_Anz_MA_Hour.VA_ID, tbl_VA_AnzTage.ID AS VADatum_ID, tbl_VA_AnzTage.VADatum, tbl_VA_Auftragstamm.Auftrag, [Objekt] & " - " & [Ort] AS Objekt_Ort, Sum(qry_Anz_MA_Hour.MA_Plan) AS MA_Plan_Ges, Sum(qry_Anz_MA_Hour.MA_Soll) AS MA_Soll_Ges, Sum(qry_Anz_MA_Hour.MA_Ist) AS MA_Ist_Ges, tbl_VA_AnzTage.TVA_Offen
FROM tbl_VA_AnzTage RIGHT JOIN (qry_Anz_MA_Hour RIGHT JOIN tbl_VA_Auftragstamm ON qry_Anz_MA_Hour.VA_ID = tbl_VA_Auftragstamm.ID) ON tbl_VA_AnzTage.VA_ID = tbl_VA_Auftragstamm.ID
GROUP BY qry_Anz_MA_Hour.VA_ID, tbl_VA_AnzTage.ID, tbl_VA_AnzTage.VADatum, tbl_VA_Auftragstamm.Auftrag, [Objekt] & " - " & [Ort], tbl_VA_AnzTage.TVA_Offen;

SELECT tbl_MA_VA_Zuordnung.VA_ID, tbl_MA_VA_Zuordnung.VAStart_ID, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.VADatum_ID, Count(tbl_MA_VA_Zuordnung.ID) AS AnzMA_Z, Max(tbl_MA_VA_Zuordnung.PosNr) AS PosNrMax
FROM tbl_MA_VA_Zuordnung
GROUP BY tbl_MA_VA_Zuordnung.VA_ID, tbl_MA_VA_Zuordnung.VAStart_ID, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.VADatum_ID;

SELECT tbl_MA_VA_Zuordnung.VA_ID, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.VADatum_ID, Count(tbl_MA_VA_Zuordnung.ID) AS AnzMA_Z, Max(tbl_MA_VA_Zuordnung.PosNr) AS PosNrMax
FROM tbl_MA_VA_Zuordnung
GROUP BY tbl_MA_VA_Zuordnung.VA_ID, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.VADatum_ID;

SELECT tbl_VA_Start.VA_ID, tbl_VA_Start.VADatum_ID, tbl_VA_Start.ID AS VAStart_ID, tbl_VA_Start.VA_Start, tbl_VA_Start.VA_Ende, Count(qry_MA_Plan.MA_ID) AS MA_Plan, tbl_VA_Start.MA_Anzahl AS MA_Soll
FROM qry_MA_Plan RIGHT JOIN tbl_VA_Start ON qry_MA_Plan.VAStart_ID = tbl_VA_Start.ID
GROUP BY tbl_VA_Start.VA_ID, tbl_VA_Start.VADatum_ID, tbl_VA_Start.ID, tbl_VA_Start.VA_Start, tbl_VA_Start.VA_Ende, tbl_VA_Start.MA_Anzahl;

SELECT tbl_MA_VA_Zuordnung.VADatum_ID, tbl_MA_VA_Zuordnung.VA_ID, Count(tbl_MA_VA_Zuordnung.ID) AS AnzSoll
FROM tbl_MA_VA_Zuordnung
GROUP BY tbl_MA_VA_Zuordnung.VADatum_ID, tbl_MA_VA_Zuordnung.VA_ID;

SELECT tbl_VA_Start.VA_ID, tbl_VA_Start.VADatum_ID, Sum(tbl_VA_Start.MA_Anzahl) AS SummevonMA_Anzahl
FROM tbl_VA_Start
GROUP BY tbl_VA_Start.VA_ID, tbl_VA_Start.VADatum_ID;

SELECT tbl_VA_Auftragstamm.*
FROM tbl_VA_Auftragstamm
ORDER BY tbl_VA_Auftragstamm.Dat_VA_Von, tbl_VA_Auftragstamm.Auftrag;

SELECT tbl_VA_Auftragstamm.Veranstalter_ID AS kun_id, tbl_KD_Kundenstamm.kun_Firma, tbl_VA_Auftragstamm.ID AS VA_ID, tbl_VA_Auftragstamm.Dat_VA_Von, tbl_VA_Auftragstamm.Dat_VA_Bis, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Ort, tbl_VA_Auftragstamm.Objekt, tbl_MA_Mitarbeiterstamm.ID AS MA_ID, tbl_MA_Mitarbeiterstamm.Nachname, tbl_MA_Mitarbeiterstamm.Vorname, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.MA_Netto_Std, tbl_MA_Mitarbeiterstamm.Stundenlohn_brutto, fctRound(Nz([MA_Netto_Std],0)*Nz([Stundenlohn_brutto],0)) AS Kosten_Ma, tbl_MA_VA_Zuordnung.PKW AS FahrtKosten, tbl_VA_Auftragstamm.Dummy, fctRound(Nz([Dummy],0)*Nz([MA_Netto_Std],0)) AS Umsatz_VA
FROM (tbl_KD_Kundenstamm RIGHT JOIN tbl_VA_Auftragstamm ON tbl_KD_Kundenstamm.kun_Id = tbl_VA_Auftragstamm.Veranstalter_ID) LEFT JOIN (tbl_MA_Mitarbeiterstamm RIGHT JOIN tbl_MA_VA_Zuordnung ON tbl_MA_Mitarbeiterstamm.ID = tbl_MA_VA_Zuordnung.MA_ID) ON tbl_VA_Auftragstamm.ID = tbl_MA_VA_Zuordnung.VA_ID
ORDER BY tbl_MA_VA_Zuordnung.VADatum DESC , tbl_VA_Auftragstamm.Dat_VA_Von DESC , tbl_VA_Auftragstamm.Dat_VA_Bis DESC , tbl_VA_Auftragstamm.Auftrag;

SELECT tbl_VA_Auftragstamm.Veranstalter_ID AS kun_id, tbl_KD_Kundenstamm.kun_Firma, tbl_VA_Auftragstamm.ID AS VA_ID, tbl_VA_Auftragstamm.Dat_VA_Von, tbl_VA_Auftragstamm.Dat_VA_Bis, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Ort, tbl_VA_Auftragstamm.Objekt, tbl_VA_Auftragstamm.Dummy, tbl_VA_Start.MA_Anzahl, tbl_VA_AnzTage.VADatum, tbl_VA_Start.VA_Start, tbl_VA_Start.VA_Ende, timeberech_G([tbl_VA_Start].[VADatum],[VA_Start],[VA_Ende]) AS Plan_Std_Brutto, Nz(timeberech_G(tbl_VA_Start.VADatum,[VA_Start],[VA_Ende]),0)*Nz([Dummy],0)*Nz([MA_Anzahl],0) AS Plan_Umsatz_pro_Zeitraum
FROM (tbl_KD_Kundenstamm RIGHT JOIN tbl_VA_Auftragstamm ON tbl_KD_Kundenstamm.kun_Id = tbl_VA_Auftragstamm.Veranstalter_ID) LEFT JOIN (tbl_VA_AnzTage LEFT JOIN tbl_VA_Start ON tbl_VA_AnzTage.ID = tbl_VA_Start.VADatum_ID) ON tbl_VA_Auftragstamm.ID = tbl_VA_AnzTage.VA_ID
ORDER BY tbl_VA_AnzTage.VADatum DESC , tbl_VA_Start.VA_Start, tbl_VA_Auftragstamm.Dat_VA_Von DESC , tbl_VA_Auftragstamm.Dat_VA_Bis DESC , tbl_VA_Auftragstamm.Auftrag;

SELECT ma.Nachname & ', ' & ma.Vorname AS Mitarbeiter, nv.vonDat, Format(nv.vonDat,'mmm') AS Monat
FROM tbl_MA_Mitarbeiterstamm AS ma INNER JOIN tbl_MA_NVerfuegZeiten AS nv ON ma.ID = nv.MA_ID
WHERE ma.Anstellungsart_ID IN (3,5) AND nv.Zeittyp_ID = 'Krank' AND Year(nv.vonDat) = 2023;

