SELECT tbl_KD_Kundenstamm.kun_Id, tbl_KD_Kundenstamm.kun_Firma, tbl_KD_Kundenstamm.kun_Bezeichnung, tbl_KD_Ansprechpartner.adr_Name1, tbl_KD_Kundenstamm.kun_Strasse, tbl_KD_Kundenstamm.kun_PLZ, tbl_KD_Kundenstamm.kun_Ort, tbl_KD_Kundenstamm.kun_BriefKopf, tbl_KD_Kundenstamm.kun_LKZ, [_tblLKZ].Landesname_deu
FROM _tblLKZ RIGHT JOIN (tbl_KD_Kundenstamm LEFT JOIN tbl_KD_Ansprechpartner ON tbl_KD_Kundenstamm.kun_IDF_PersonID = tbl_KD_Ansprechpartner.adr_ID) ON [_tblLKZ].ISO_2 = tbl_KD_Kundenstamm.kun_LKZ
WHERE (((tbl_KD_Kundenstamm.kun_ans_manuell)=False) AND ((tbl_KD_Kundenstamm.kun_AdressArt)=1));
