SELECT Null AS VA_ID, Null AS VADatum_ID, Null AS VAStart_ID, Null AS ZUO_ID, Null AS NV_ID, ztbl_MA_ZK_Korrekturen_FE.ID AS Korr_ID, ztbl_MA_ZK_Korrekturen_FE.MA_ID, "Korrektur" AS Veranstaltung, ztbl_MA_ZK_Korrekturen_FE.vonDat AS VADatum, ztbl_MA_ZK_Korrekturen_FE.Jahr, ztbl_MA_ZK_Korrekturen_FE.Monat AS Monat, ztbl_MA_ZK_Korrekturen_FE.Lohnart_ID, Round(([ztbl_MA_ZK_Korrekturen_FE].[Anz_Std]*1.098901099),4) AS Anz_Std, ztbl_MA_ZK_Korrekturen_FE.Satz, ztbl_MA_ZK_Korrekturen_FE.Wert, Null AS Beginn, Null AS Ende, ztbl_MA_ZK_Korrekturen_FE.gesperrt, False AS exportiert, ztbl_MA_ZK_Korrekturen_FE.erstellt, ztbl_MA_ZK_Korrekturen_FE.Ersteller, "" AS geaendert, "" AS Aenderer, True AS exportieren, False AS Korrektur, ztbl_MA_ZK_Korrekturen_FE.Bemerkung, tbl_MA_Mitarbeiterstamm.Anstellungsart_ID
FROM ztbl_MA_ZK_Korrekturen_FE INNER JOIN tbl_MA_Mitarbeiterstamm ON ztbl_MA_ZK_Korrekturen_FE.Ma_ID = tbl_MA_Mitarbeiterstamm.ID
ORDER BY ztbl_MA_ZK_Korrekturen_FE.vonDat;
