SELECT ztbl_MA_VA_Zuordnung_FE.VA_ID, ztbl_MA_VA_Zuordnung_FE.VADatum_ID, ztbl_MA_VA_Zuordnung_FE.VAStart_ID, ztbl_MA_VA_Zuordnung_FE.ID AS ZUO_ID, Null AS NV_ID, Null AS Korr_ID, ztbl_MA_VA_Zuordnung_FE.MA_ID, tbl_VA_Auftragstamm.Auftrag AS Veranstaltung, ztbl_MA_VA_Zuordnung_FE.VADatum, Year([VADatum]) AS Jahr, Month([vadatum]) AS Monat, 25 AS Lohnart_ID, ztbl_MA_VA_Zuordnung_FE.pkw AS Anz_Std, 1 AS Satz, ([Anz_Std]*[Satz]) AS Wert, ztbl_MA_VA_Zuordnung_FE.MA_Start AS Beginn, ztbl_MA_VA_Zuordnung_FE.MA_Ende AS Ende, False AS gesperrt, False AS exportiert, Now() AS erstellt, ermittle_Benutzer() AS Ersteller, "" AS geaendert, "" AS Aenderer, True AS exportieren, False AS Korrektur, ztbl_MA_VA_Zuordnung_FE.Bemerkungen AS Bemerkung, tbl_MA_Mitarbeiterstamm.Anstellungsart_ID
FROM (ztbl_MA_VA_Zuordnung_FE INNER JOIN tbl_VA_Auftragstamm ON ztbl_MA_VA_Zuordnung_FE.VA_ID = tbl_VA_Auftragstamm.ID) INNER JOIN tbl_MA_Mitarbeiterstamm ON ztbl_MA_VA_Zuordnung_FE.MA_ID = tbl_MA_Mitarbeiterstamm.ID
WHERE (((ztbl_MA_VA_Zuordnung_FE.pkw)<>0));
