-- Query: qry_Rch_Mahnstufe3
-- Type: 0
SELECT tbl_Rch_Kopf.*, tbl_KD_Kundenstamm.kun_AdressArt, tbl_KD_Kundenstamm.kun_Firma, tbl_KD_Kundenstamm.kun_Bezeichnung, tbl_KD_Kundenstamm.kun_Matchcode, tbl_KD_Kundenstamm.kun_Strasse, tbl_KD_Kundenstamm.kun_PLZ, tbl_KD_Kundenstamm.kun_Ort, tbl_KD_Kundenstamm.kun_LKZ, tbl_KD_Kundenstamm.kun_BriefKopf, tbl_KD_Kundenstamm.kun_ans_manuell, tbl_KD_Kundenstamm.kun_Anschreiben, tbl_KD_Kundenstamm.kun_land_vorwahl, tbl_KD_Kundenstamm.kun_telefon, tbl_KD_Kundenstamm.kun_telefax, tbl_KD_Kundenstamm.kun_mobil, tbl_KD_Kundenstamm.kun_email, tbl_KD_Kundenstamm.kun_URL, tbl_KD_Kundenstamm.kun_kreditinstitut, tbl_KD_Kundenstamm.kun_blz, tbl_KD_Kundenstamm.kun_kontonummer, tbl_KD_Kundenstamm.kun_iban, tbl_KD_Kundenstamm.kun_bic, tbl_KD_Kundenstamm.kun_ustidnr, tbl_KD_Kundenstamm.kun_memo, tbl_KD_Kundenstamm.kun_Probleme, tbl_KD_Kundenstamm.kun_IDF_PersonID, tbl_KD_Kundenstamm.kun_Zahlbed, tbl_KD_Kundenstamm.kun_IstSammelRechnung
FROM tbl_Rch_Kopf INNER JOIN tbl_KD_Kundenstamm ON tbl_Rch_Kopf.kun_ID = tbl_KD_Kundenstamm.kun_Id
WHERE (((tbl_Rch_Kopf.RchTyp) In (4,5,6)) AND ((([Zahlung_Bis]+fMahnDat(3)))<Date()) AND ((tbl_Rch_Kopf.M3IstGemahnt1)=False) AND ((tbl_Rch_Kopf.kun_ID)>0) AND ((tbl_Rch_Kopf.M1IstGemahnt1)=True) AND ((tbl_Rch_Kopf.M2IstGemahnt1)=True));

