-- Query: qry_Textbaustein_Auftrag
-- Type: 0
SELECT tbl_VA_Auftragstamm.ID AS VA_ID, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Veranstalter_ID, tbl_VA_Auftragstamm.Objekt, tbl_VA_Auftragstamm.Strasse, tbl_VA_Auftragstamm.PLZ, tbl_VA_Auftragstamm.Ort, CStr(Format([Dat_VA_Von],"ddd  dd/mm/yyyy")) AS Datum_VA_Von, CStr(Format([Dat_VA_Bis],"ddd  dd/mm/yyyy")) AS Datum_VA_Bis, tbl_VA_Auftragstamm.Dummy, tbl_VA_Auftragstamm.Bemerkungen, CStr(Format([Rch_Dat],"ddd  dd/mm/yyyy")) AS Rch_Datum, tbl_VA_Auftragstamm.Rch_Nr, tbl_VA_Auftragstamm.Bemerkung, tbl_VA_Auftragstamm.Treffpunkt, tbl_VA_Auftragstamm.Treffpunkt2, CStr(Format([Treffp_Zeit],"Short Time")) & " Uhr" AS Treffpunkt_Zeit, tbl_VA_Auftragstamm.Dienstkleidung, tbl_VA_Auftragstamm.Ansprechpartner, tbl_VA_Auftragstamm.Veranst_Status_ID, tbl_VA_Auftragstamm.Objekt_ID, tbl_VA_Status.Fortschritt AS Auftragsstatus, tbl_VA_Start.VADatum_ID, tbl_VA_Start.ID AS VAStart_ID, tbl_VA_Start.VADatum, CStr(Format([VADatum],"ddd dd/mm/yyyy")) AS VADatum1, CStr(Format([VA_Start],"Short Time")) & " Uhr" AS VA1_Start, CStr(Format([VA_Ende],"Short Time")) & " Uhr" AS VA1_Ende, tbl_KD_Kundenstamm.kun_Firma AS A_Auftraggeber, qry_Textbaustein_Kunde.kun_Id, qry_Textbaustein_Kunde.kun_AdressArt, qry_Textbaustein_Kunde.kun_Firma, qry_Textbaustein_Kunde.kun_Bezeichnung, qry_Textbaustein_Kunde.kun_Matchcode, qry_Textbaustein_Kunde.kun_Strasse, qry_Textbaustein_Kunde.kun_PLZ, qry_Textbaustein_Kunde.kun_Ort, qry_Textbaustein_Kunde.kun_LKZ, qry_Textbaustein_Kunde.kun_BriefKopf, qry_Textbaustein_Kunde.kun_ans_manuell, qry_Textbaustein_Kunde.kun_Anschreiben, qry_Textbaustein_Kunde.kun_land_vorwahl, qry_Textbaustein_Kunde.kun_telefon, qry_Textbaustein_Kunde.kun_telefax, qry_Textbaustein_Kunde.kun_mobil, qry_Textbaustein_Kunde.kun_email, qry_Textbaustein_Kunde.kun_URL, qry_Textbaustein_Kunde.kun_kreditinstitut, qry_Textbaustein_Kunde.kun_blz, qry_Textbaustein_Kunde.kun_kontonummer, qry_Textbaustein_Kunde.kun_iban, qry_Textbaustein_Kunde.kun_bic, qry_Textbaustein_Kunde.kun_ustidnr
FROM (qry_Textbaustein_Kunde RIGHT JOIN ((tbl_KD_Kundenstamm RIGHT JOIN tbl_VA_Auftragstamm ON tbl_KD_Kundenstamm.kun_Id = tbl_VA_Auftragstamm.Veranstalter_ID) LEFT JOIN tbl_VA_Status ON tbl_VA_Auftragstamm.Veranst_Status_ID = tbl_VA_Status.ID) ON qry_Textbaustein_Kunde.kun_Id = tbl_VA_Auftragstamm.Veranstalter_ID) LEFT JOIN tbl_VA_Start ON tbl_VA_Auftragstamm.ID = tbl_VA_Start.VA_ID
ORDER BY tbl_VA_Auftragstamm.Dat_VA_Von, tbl_VA_Auftragstamm.Auftrag;

