SELECT tbl_KD_Standardpreise.StdPreis, tbl_KD_Standardpreise.Preisart_ID, tbl_VA_Auftragstamm.Auftrag, tbl_VA_Auftragstamm.Veranstalter_ID, tbl_VA_Auftragstamm.Objekt, tbl_VA_Auftragstamm.Ort, tbl_VA_Auftragstamm.Dat_VA_Von, tbl_VA_Auftragstamm.Dat_VA_Bis, tbl_VA_Auftragstamm.Rch_Nr, tbl_VA_Auftragstamm.Rch_Dat, tbl_VA_Auftragstamm.Veranst_Status_ID, tbl_VA_Auftragstamm.Objekt_ID, tbl_VA_Auftragstamm.ID, tbl_MA_VA_Zuordnung.MA_ID, tbl_MA_VA_Zuordnung.MA_Start, tbl_MA_VA_Zuordnung.MA_Ende, tbl_MA_VA_Zuordnung.MA_Brutto_Std, tbl_MA_VA_Zuordnung.MA_Netto_Std, tbl_MA_VA_Zuordnung.RL_34a, tbl_MA_VA_Zuordnung.VADatum, tbl_MA_VA_Zuordnung.MA_Brutto_Std2, tbl_MA_VA_Zuordnung.MA_Netto_Std2, tbl_MA_VA_Zuordnung.RL34a, tbl_MA_Mitarbeiterstamm.Nachname, tbl_MA_Mitarbeiterstamm.Vorname, tbl_MA_Mitarbeiterstamm.Anstellungsart_ID, tbl_MA_Mitarbeiterstamm.Stundenlohn_brutto, tbl_MA_Mitarbeiterstamm.Ist_RV_Befrantrag, tbl_MA_Mitarbeiterstamm.Arbst_pro_Arbeitstag, tbl_MA_Mitarbeiterstamm.Arbeitstage_pro_Woche, tbl_MA_Mitarbeiterstamm.Urlaubsanspr_pro_Jahr, tbl_MA_Mitarbeiterstamm.StundenZahlMax, tbl_MA_Mitarbeiterstamm.Hat_keine_34a, ([Kosten_pro_MAStunde]*[MA_Netto_Std2]) AS Personalkosten, ([StdPreis]*[MA_Brutto_Std2]) AS Umsatz
FROM (tbl_MA_VA_Zuordnung INNER JOIN (tbl_KD_Standardpreise INNER JOIN tbl_VA_Auftragstamm ON tbl_KD_Standardpreise.kun_ID = tbl_VA_Auftragstamm.Veranstalter_ID) ON tbl_MA_VA_Zuordnung.VA_ID = tbl_VA_Auftragstamm.ID) INNER JOIN tbl_MA_Mitarbeiterstamm ON tbl_MA_VA_Zuordnung.MA_ID = tbl_MA_Mitarbeiterstamm.ID;

